import NewPost from "./form/NewPost";
import Post from "./Post";
import { useState } from "react";
import Axios from "axios";
import Navbar from "./layout/Navbar";
import Footer from "./layout/Footer";
import "./Feed.modules.css";
import fotoRadar from "../img/jinx.jpeg"
import iconeCurtir from "../img/iconeHeart.png"
import iconeCurtido from "../img/iconeHeartFull.png"


function Feed() {
  const [postList, setPostList] = useState([]);
  const [followers, setFollowers] = useState([]);
  let id = parseInt(localStorage.getItem("id"));
  let posts = [];
  let jogos = [];
  let follows = [];
  let trocaIcone = 0;

  const getPosts = () => {
    Axios.get("http://localhost:3001/posts").then((response) => { //seleciona todos os dados de post
      setPostList(response.data);
    });

    Axios.get("http://localhost:3001/followers").then((response) => { //pega as informações de seguidores
      setFollowers(response.data);
    });

    let i = 0;
    while (i < followers.length) {    //seleciona os usuários e grupos seguidos pelo user logado
      if (id === followers[i].id /*&& followers[i].grupo === 0*/) {
        follows.push([followers[i].userID, followers[i].grupo])
      }
      i = i + 1;
    }
    //console.log(follows)

    i = 0;
    //let j = 0;
    while (i < postList.length) {
      //while (j < follows.length){
      if ((follows[0].includes(postList[i].userNameID, 0) || follows[0].includes(postList[i].originalPosterID, 0) || postList[i].userNameID === id || postList[i].originalPosterID === id) && follows[1] == postList[i].grupo) {
        posts.push(postList[i]);
      }
      //j = j + 1;
      //}
      i = i + 1;
      //console.log(i, j)
    }
    
    function trocar() {
      if (trocaIcone == 0){ //reseta quando o contatador for igual ao tamanho da array e volta a 1° img
        trocaIcone = 1;
      }
      document.getElementById("imgCurtir").src={iconeCurtido}; //altera a img do elemento "bomdia" de acordo com o indice
    }

  }
  //console.log(posts)

  getPosts();

  return (
    <>
      <Navbar />

      <div className="feedPrincipal">
        <div className="postsFeed">
          <NewPost />
          {posts.map((val, key) => {
            return (
              <Post user={val.userName} texto={val.text} id={val.id} />
            );
          })}
        </div>
        {/** Caixas lateral - Descubra e radar */}
        <div className="lateral">
          {/** Caixa Descubra*/}
          <div className="descubra">
            <h1>Descubra</h1>
            <div className="linha"> {/**Da para usar looping para repetir 4 vezes? */}
              <div className="jogo"><p>Jogo 01</p></div>
              <div className="btnSeguir"><p>Seguir</p></div>
            </div>
            <div className="linha">
              <div className="jogo"><p>Jogo 02</p></div>
              <div className="btnSeguir"><p>Seguir</p></div>
            </div>
            <div className="linha">
              <div className="jogo"><p>Jogo 03</p></div>
              <div className="btnSeguir"><p>Seguir</p></div>
            </div>
            <div className="linha">
              <div className="jogo"><p>Jogo 04</p></div>
              <div className="btnSeguir"><p>Seguir</p></div>
            </div>
            <div className="linha">
              <div className="btnExplorar"><p>Explorar mais na fybs...</p></div>
            </div>
          </div>
          {/** Caixa Radar 01*/}
          <h1>Radar</h1>
          <div className="radar">
            {/** user e botao seguir*/}
            <div className="userRadar">
              <div className="userPostR"><p>@teste</p></div>
              <div className="btnSeguir"><p>Seguir</p></div>
            </div>
            {/** foto do radar*/}
            <div className="postRadar">
              <div className="jogo">
                <img src={fotoRadar} alt="..." width="100%" />
              </div>
            </div>
            {/** likes do post*/}
            <div className="acoesRadar">
              <div className="btnCurtir">
                <img src={iconeCurtir} alt="..." width="15px" />
              </div>
              <div className="txtLikes">
                <p>100 likes</p>
              </div>
            </div>
          </div>
          {/** Caixa Radar 02 */}
          <div className="radar">
            {/** user e botao seguir*/}
            <div className="userRadar">
              <div className="userPostR"><p>@teste</p></div>
              <div className="btnSeguir"><p>Seguir</p></div>
            </div>
            {/** foto do radar*/}
            <div className="postRadar">
              <div className="jogo">
                <img src={fotoRadar} alt="..." width="100%" />
              </div>
            </div>
            {/** likes do post*/}
            <div className="acoesRadar">
              <div className="btnCurtir">
                <img src={iconeCurtir} alt="..." width="15px" onclick="curtir" className="imgCurtir" />
              </div>
              <div className="txtLikes">
                <p>100 likes</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      <Footer />

    </>
  )
}

export default Feed;
