import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Perfil.modules.css";
import Axios from "axios";
import Navbar from "./layout/Navbar";
import Footer from "./layout/Footer";
import Globais from "./Globais";
import axios from "axios";

function PerfilAlterar(){  //falta salvar as alterações no banco de dados
 
    let userName = Globais.user;
    const [newUser, setNewUser] = useState();
    const [newEmail, setNewEmail] = useState();
    const [newDescricao, setNewDescricao] = useState();
    const [imagem, setImagem] = useState();
    
    // let email = "";
    // let descricao = "";
    // let image = "";
    let id = Globais.id;
    
    const navigate = useNavigate()
    
    const [perfil, setPerfil] = useState();
    
    const getProfile = () => {
        Axios.get("http://localhost:3001/users").then((response) => { //pega os dados do backend
        setPerfil(response.data);
        //console.log("teste: ", newUser, newEmail, newDescricao, imagem);
      });

      let i = 0;
      while (i < perfil.length){    //encontra os dados através do user conectado
        if (userName === perfil[i].nome) {
            descricao = perfil[i].descricao;
            email = perfil[i].email;
            image = perfil[i].imagem;
            i = perfil.length;
        }
        i = i + 1;

    } 
    }

    

    const alterarDados = () => {        
        
        if (newUser === "") { setNewUser(userName) };
        if (newEmail === "") { setNewEmail(email)};
        if (newDescricao === "") { setNewDescricao(descricao)};
        if (imagem === "") { setImagem(image)};        
        
        Axios.post("http://localhost:3001/alterardados", { //essa função envia os dados para o backend
                id: id,
                user: newUser,
                email: newEmail,
                descricao: newDescricao,
                imagem: imagem,
            }).then(() => {
                console.log("success");
            }); 
            navigate("/perfil");
    }
    

    

    return (
        <div>
            <Navbar />

            {getProfile()}
            
            <h1>Perfil</h1>                  
            <div>
                <label  className="profile">Foto de perfil</label>
                <input 
                type="file" 
                id="newimage" 
                name="newimage" 
                accept="image/*" 
                onChange={(event) => {
                      setImagem(event.target.value);}}/>
            </div>
            <div>
                <label  className="profile">Nome de usuário</label>
                <input
                type="text"
                text="nome_de_usuario"
                name="newUser"
                placeholder={userName}
                onChange={(event) => {
                    setNewUser(event.target.value);
                }}
                />
            </div>
            <div>
                <label className="profile">Email</label>
                <input
                type="text"
                text="email"
                name="email"
                placeholder={email}
                onChange={(event) => {
                    setNewEmail(event.target.value);
                }}
                />
            </div>
            <div>
                <label className="profile">Descrição</label>
                <input
                type="descricao"
                text="descricao"
                name="newDescricao"
                placeholder={descricao}
                onChange={(event) => {
                    setNewDescricao(event.target.value);
                }}
                />
            </div>
            
            <div>
                <button onClick={alterarDados}>Salvar alterações</button>
            </div>
        < Footer />
        </div>
    );
}

export default PerfilAlterar;
