import "./Post.modules.css";
import { Link, useNavigate } from 'react-router-dom'
import { useState } from "react";
import Axios from "axios";
import blackheart from "../img/blackheart.png"
import redheart from "../img/redheart.png"
import blackshare from "../img/blackshare.png"
import blueshare from "../img/blueshare.png"

function Post({ user, texto, id }) {
    const [perfil, setPerfil] = useState([]);
    const [likeList, setLikeList] = useState([]);
    const navigate = useNavigate();
    let userid = parseInt(localStorage.getItem("id"));
    let fotoPerfil = "";

    let heart = blackheart;
    let share = blackshare;

    Axios.get("http://localhost:3001/users").then((response) => { 
        setPerfil(response.data);
      });
      let i = 0;
      while (i < perfil.length){   
          if (user === perfil[i].nome) {
              fotoPerfil = perfil[i].foto; //seta a foto de perfil do dono do post
          }
          i = i + 1;
       } 
  
    Axios.get("http://localhost:3001/likes").then((response) => { //seleciona todos os dados de likes
        setLikeList(response.data);
    });
    
    const darLike = () =>{
        while (i < likeList.length){
            if (likeList[i].userID === userid && likeList[i].postId === id) {
                console.log("like")
            } 
            i = i + 1;
        } /*
        if (heart === blackheart){
            heart = redheart;
            console.log("Vermelho");
        }
        else {
            heart = blackheart;
            console.log("Preto");
        }*/
        //window.location.reload()
    }

    return (
        <div className="post">
            <div className="userPost">
                <img className="profile-pic mr-3" src={fotoPerfil} />
                <button className="btnUser"><a class="btnUser" href="/verperfil/set">{user}</a></button>
                <div className="corpoPost">
                    <p className="texto">{texto}</p>
                </div>
                <div className="btnIntercoes">
                <button onClick={darLike}><img className="profile-pic mr-3" src={heart} /></button>
                <button><img className="profile-pic mr-3" src={share} /></button>
                <img className="profile-pic mr-3" src="https://i.pinimg.com/236x/1e/04/fd/1e04fd17f397b0513736628602fd9583.jpg" /> 
                </div>
            </div>

        </div>
    )
}

export default Post;
