import "./Post.modules.css";
import { Link, useNavigate } from 'react-router-dom'
import Perfil from "./Perfil";
import Perfil2 from './Perfil2';
import Globais from "./Globais";
import VerPerfil from "./VerPerfil";

function Post({ user, texto, id }) {
    const navigate = useNavigate();

    const abrirPerfil = (user) => {
        Globais.user2 = user;
        //navigate('/verperfil/set');
    }
    return (
        <div className="post">
            <div className="userPost">
                <img className="profile-pic mr-3" src="https://i.pinimg.com/236x/1e/04/fd/1e04fd17f397b0513736628602fd9583.jpg" />
                <button className="btnUser" onClick={abrirPerfil(user)}>{user}</button>
                <div className="corpoPost">
                    <p className="texto">{texto}</p>
                </div>
                <div className="btnIntercoes">
                <img className="profile-pic mr-3" src="https://i.pinimg.com/236x/1e/04/fd/1e04fd17f397b0513736628602fd9583.jpg" />
                <img className="profile-pic mr-3" src="https://i.pinimg.com/236x/1e/04/fd/1e04fd17f397b0513736628602fd9583.jpg" />
                <img className="profile-pic mr-3" src="https://i.pinimg.com/236x/1e/04/fd/1e04fd17f397b0513736628602fd9583.jpg" /> 
                </div>
            </div>

        </div>
    )
}

export default Post;

