import "./NewPost.modules.css"
import SubmitButton from "./SubmitButton";
import cam from "../cam.png"
import { useState } from "react";
import Axios from "axios";
import Globais from "../Globais";


function NewPost(){
    const [texto, setTexto] = useState();    
    const [imagem, setImagem] = useState();
    const [user, setUser] = useState(Globais.user); //coloquei esse user padrão pq ainda não sei como pegar o usuário logado

    const sendPost = () => {
        console.log(Globais.user)
        Axios.post("http://localhost:3001/createpost", { //essa função envia os dados para o backend
        texto: texto,
        user: user,
        imagem: imagem,
        }).then(() => {
            console.log("success"); 
        });
    }

    return (
        <form className="form">
            <div className="container px-4 py-5 mx-auto">
                <div className="row d-flex justify-content-center">
                    <div className="card">
                        <div className="row px-3">
                            <img className="profile-pic mr-3" src="https://i.imgur.com/6tPhTUn.jpg" />
                            <div className="flex-column">
                                <h3 className="mb-0 font-weight-normal">Camilla Perez</h3>
                                <select name="privacy" className="privacy">
                                    <option>Public post</option>
                                    <option>Private post</option>
                                </select>
                            </div>
                        </div>
                        <div className="row px-3 form-group">
                            <textarea className="text-muted bg-light mt-4 mb-3" placeholder="Hi Camilla, what's on your mind today?"></textarea>
                        </div>
                        <div className="row px-3">
                            <p className="fa fa-user options mb-0 mr-4"></p>
                            <p className="fa fa-map-marker options mb-0 mr-4"></p>
                            <p className="fa fa-image options mb-0 mr-4"></p>
                            <img className="options" src="https://img.icons8.com/material/24/000000/more--v2.png" width="30px" height="28px" />
                            <div className="btn btn-success ml-auto">Post</div>
                        </div>
                    </div>
                </div>
            </div>


            <div className="nvPost">

                <textarea name="texto" placeholder="O que você está jogando hoje?" onChange={(event) => {
                    setTexto(event.target.value);
                }}>
                </textarea>
            </div>
            <div className="nvPost"></div>
            <div className="imageContainer">
                <img src={cam} alt="Selecione uma imagem" id="imgphoto"></img>
                <input type="file" id="postimage" name="postimage" accept="image/*" onChange={(event) => {
                    setImagem(event.target.value);
                }}></input>
            </div>
            <div className="form_control">
                <button className="btn" onClick={sendPost}>Publicar</button>
            </div>
        </form>
    )
}

export default NewPost;
