import { useState } from "react";
import "./Perfil.modules.css";
import Axios from "axios";
import { Link } from 'react-router-dom';
import Globais from "./Globais";
import Navbar2 from "./layout/Navbar2";
import Footer from "./layout/Footer";
import Post from "./Post";
import profileimage from '../img/user.png'

function Perfil({user}){

    /********************************************************************************************************************/
    //Parte lógica
    const [postList, setPostList] = useState([]);
    let posts = [];

    let userName = "";
    let id = parseInt(localStorage.getItem("id"));
    let email = "";
    let descricao = "";
    let imagem = "";

    let seguidores = 0;
    let seguindo = 0;

    const [perfil, setPerfil] = useState([]);
    const [followers, setFollowers] = useState([]);

    const getProfile = () => {
        Axios.get("http://localhost:3001/users").then((response) => { //pega os dados do backend
          setPerfil(response.data);
        });
        let i = 0;
        while (i < perfil.length){    //encontra os dados através do user conectado
            if (id === perfil[i].id) {
                //console.log("achei")
                descricao = perfil[i].descricao;
                email = perfil[i].email;
                imagem = perfil[i].foto;
                userName = perfil[i].nome;
                i = perfil.length;
            }
            i = i + 1;
         } 

         Axios.get("http://localhost:3001/followers").then((response) => { //pega os dados do backend
            setFollowers(response.data);
        });

        i = 0;
        while (i < followers.length){    //encontra os dados através do user conectado
            if (id === followers[i].id && followers[i].grupo === 0) {
                seguidores = seguidores + 1;
            } 
            if (id === followers[i].id && followers[i].grupo === 0) {
                seguindo = seguindo + 1;
            }
            i = i + 1;
         } 

         Axios.get("http://localhost:3001/posts").then((response) => { //pega os dados dos posts
         //console.log("Função getPosts: ", postList);
         setPostList(response.data);
         });
         i = 0;
         while (i < postList.length) {
             if (postList[i].originalPosterID === id) { 
             //mostrar posts aqui
             //publicacoes += 1;
             posts.push(postList[i]);
             }
             i = i + 1;  
         }
    }

    getProfile();

    /********************************************************************************************************************/
    //Parte visual
    return (
        <div>
            < Navbar2 />

            <h1>Perfil</h1>
            <div>
            <div>   
                <img src={imagem} width="80"></img>
            </div>
            <div>
                <label  className="profile">Nome de usuário</label>
                <label className="profile">@{userName}</label>
            </div>
            <div>
                <label className="profile">Email</label>
                <label className="profile">{email}</label>
            </div>
            <div>
                <label className="profile">Descrição</label>
                <label className="profile">{descricao}</label>
            </div>
            <label  className="profile">Seguidores</label>
                <label className="profile">@{seguidores}</label>
            </div>
            <div>
                <label  className="profile">Seguindo</label>
                <label className="profile">@{seguindo}</label>
            </div>
            <button><Link to="/alterarperfil">Alterar dados</Link></button>
            {/*Posts*/}
              <div className="py-4 px-4">
                <div className="d-flex align-items-center justify-content-between mb-3">
                 <h5 className="mb-0">Publicações</h5>
                </div>
                <div className="posts">
                {posts.map((val, key) => {
                return (
                <Post user={'@' + val.userName} texto={val.text} />
                );
                })}
              </div>
            </div>
            <Footer />
        </div>
    );
}

export default Perfil;

