import NewPost from "./form/NewPost";
import Post from "./Post";
import { useState } from "react";
import Axios from "axios";
import "./Grupos.modules.css";
import Navbar from "./layout/Navbar";
import Footer from "./layout/Footer";
import React from 'react'
import ftgrupo from './kokomi_by_IE.jpg';

function Grupos(){
  // javascripto
    const [postList, setPostList] = useState([]);

    const getPosts = () => {
      Axios.get("http://localhost:3001/posts").then((response) => { //pega os dados do backend
        //console.log("Função getPosts: ", postList);
        setPostList(response.data);
    });
    }

    getPosts();

    // html e css
    return ( 
        <><div className="feedGrupos">
        <Navbar />
        <div className="perfilGrupo">
          <div className="fotoGrupo">
            <img src={ftgrupo}></img>
          </div>
          <p>aqui dicara o perfil do grupo!!</p>
        </div>
        <NewPost />
        {postList.map((val, key) => {
          return (
            <Post user={'@' + val.userName} texto={val.text} />
          );
        })}
      </div><Footer /></>        
        
    )
}

export default Grupos;
